import SelectPlayer from './SelectPlayer'
import axios from 'axios'
import { useMemo, useState, useEffect } from 'react'

const chartData = []

const SelectPlayerController = () => {
  const [data, setData] = useState([])
  const comboBoxData = []

  const fetchData = () => {
    axios
      .get('http://localhost:3000/player/players')
      .then((res) => res.data)
      .then((data) => {
        setData(data)
        console.log(data)
      })
  }

  if (data === undefined) {
    fetchData()
  }

  const addDataChart = () => {
    data?.forEach((element) => {
      if (element.current_club === 'Liverpool') {
        comboBoxData.push({
          playerName: element.full_name,
        })
      }
    })

    console.log(comboBoxData)
  }

  useMemo(() => {
    if (comboBoxData.length === 0) {
      addDataChart()
    }
  }, [data])

  return (
    <div>
      {' '}
      <SelectPlayer data={chartData} />{' '}
    </div>
  )
}

export default SelectPlayerController
