import React, { useMemo, useEffect, useState } from 'react'

import TextField from '@mui/material/TextField'
import Autocomplete from '@mui/material/Autocomplete'
import Player from './Player'


const SelectPlayer = (props) => {
  const [playerArray, setPlayerArray] = useState([])
  const handleChange = ( _, value) => {
      if(noDuplicate(value.full_name))
      setPlayerArray([...playerArray, value])
    
  }

  const noDuplicate = (playerName) => {
    playerArray.forEach((player) => {if(player.full_name == playerName ) return false})
    return true;
  }

  return (
    <div className="selectPlayerContainer">
      <Autocomplete
        onChange={handleChange}
        disablePortal
        id="combo-box-demo"
        options={props.data}
        getOptionLabel={(option) => (option ? option.full_name : '')}
        sx={{ width: 300 }}
        renderInput={(params) => (
          <TextField {...params} label="Select Player" />
        )}
      />
      <Player data={playerArray}/>
    </div>
  )
}

export default SelectPlayer
