import React, {useMemo, useEffect, useState } from 'react'

import TextField from '@mui/material/TextField'
import Autocomplete from '@mui/material/Autocomplete'
import axios from 'axios'

const SelectPlayer = () => {
  const [data, setData] = useState([])

  const comboBoxData = []

  const fetchData = useMemo(() => {
    axios
      .get('http://localhost:3000/player/players')
      .then((res) => res.data)
      .then((data) => {
        setData(data)
        console.log(data)
      })
  },[data])

  if (data === undefined) {
    fetchData()
  }

  const addDataChart = () => {
    data?.forEach((element) => {
      if (element.current_club === 'Liverpool') {
        comboBoxData.push({
          playerName: element.full_name,
        })
      }
    })

    console.log(comboBoxData)
  }

  useEffect(() => {
    if (comboBoxData.length === 0) {
      addDataChart()
    }
  }, [data])

  return (
    <div>
      <button>ADD data</button>
      <Autocomplete
        disablePortal
        id="combo-box-demo"
        options={comboBoxData}
        getOptionLabel={(option) => (option ? option.playerName : '')}
        sx={{ width: 300 }}
        renderInput={(params) => <TextField {...params} label="Players" />}
      />
    </div>
  )
}

export default SelectPlayer
