import React, { useState } from 'react'
import axios from 'axios'
import {
  BarChart,
  Bar,
  // Cell,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from 'recharts'
import styles from './Team.module.css'

const Team = () => {
  const [teamName, setTeamName] = useState();
  const [data, setData] = useState();
  const [winsHome, setWinsHome] = useState();

  let allTeams = [];

  const chartData = [
    {
      name: 'asd',
      winsHome: 4000,
      winsAway: 2400,
      amt: 2400,
    },

  {
      name: 'aaaaa',
      winsHome: 4000,
      winsAway: 2900,
      amt: 2400,
    },
  ]
  

  const addDataChart = () => {
    data.forEach(element => {
      chartData.push({name: element.team_name, winsHome: element.wins_home, winsAway: element.wins_away, amt: 15})
    });
  }
 
  const addWinsHome = (event) => {
    axios.get('http://localhost:3000/team/teams')
    .then(res => res.data)
    .then(data => {
      setData(data)
      console.log(data);
    })
    let a = prompt()
    setWinsHome(a)
  }

  return (
    <div className={styles.team}>
      <button onClick={addWinsHome}></ button>
      <ResponsiveContainer width="100%" height="100%">
        <BarChart
          width={500}
          height={300}
          data={chartData}
          margin={{
            top: 5,
            right: 30,
            left: 20,
            bottom: 5,
          }}
        >
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="name" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Bar dataKey="winsAway" fill="#8884d8" />
          <Bar dataKey="winsHome" fill="#82ca9d" />
        </BarChart>
      </ResponsiveContainer>
    </div>
  )
}

export default Team
