import SelectPlayer from './SelectPlayer'
import axios from 'axios'
import { useMemo, useState, useEffect } from 'react'

const chartData = []
const comboBoxData = []

const SelectPlayerController = () => {
  const [data, setData] = useState([])
  

  const fetchData = () => {
    axios
      .get('http://localhost:3000/player/players')
      .then((res) => res.data)
      .then((data) => {
        const filteredData = data.filter(({player}) => ({playerName: player.current_club}) === "Liverpool")
        setData(filteredData)
        console.log(filteredData)
      })
  }

  if (data === undefined) {
    fetchData()
  }

  const addDataChart = () => {
    data?.forEach((element) => {
        comboBoxData.push({
          playerName: element.full_name,
        })
    })

    console.log(comboBoxData)
  }

  useEffect(() => {
    if (comboBoxData.length === 0) {
      addDataChart()
    }
  }, [data])

  return (
    <div>
      {' '}
      <SelectPlayer data={comboBoxData} />{' '}
    </div>
  )
}

export default SelectPlayerController
