import React, { useEffect, useState } from 'react'
import axios from 'axios'
import {
  BarChart,
  Bar,
  // Cell,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from 'recharts'
import styles from './Team.module.css'

const Team = () => {
  const [teamName, setTeamName] = useState();
  const [data, setData] = useState();
  const [winsHome, setWinsHome] = useState();
  const [chartData,setChartData] = useState([]); 
  
  const addWinsHome = (event) => {
    axios.get('http://localhost:3000/team/teams')
    .then(res => res.data)
    .then(data => {
      setData(data)
      console.log(data);
    })
  }

  if(data === undefined){
    addWinsHome()
  }

   const addDataChart = () => {
    data?.forEach(element => {
      chartData.push({name: element.team_name, winsHome: element.wins_home, winsAway: element.wins_away, amt: 15})
    });
    console.log(chartData);
  }
 

  useEffect(() => {
    addDataChart()
  },[data])

  return (
    <div className={styles.team}>
      <button onClick={addDataChart}>Add data chart</ button>
      <ResponsiveContainer width="100%" height="100%">
        <BarChart
          width={500}
          height={300}
          data={chartData}
          margin={{
            top: 5,
            right: 30,
            left: 20,
            bottom: 5,
          }}
        >
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="name" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Bar dataKey="winsAway" fill="#8884d8" />
          <Bar dataKey="winsHome" fill="#82ca9d" />
        </BarChart>
      </ResponsiveContainer>
    </div>
  )
}

export default Team
